import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { Calendar, MapPin, ExternalLink } from 'lucide-react';

const Experience: React.FC = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  const experiences = [
    {
      title: 'Head of Communications',
      company: 'Metaplex Foundation',
      location: 'Remote',
      period: 'Jan 2025 - Present',
      description: 'Lead all social media and public relations efforts across the company, setting strategy, tone, and messaging across earned and owned channels. Craft narrative frameworks to position Metaplex in Web3, gaming, and entertainment conversations.',
      technologies: ['Strategic Communications', 'Narrative Development', 'PR Strategy', 'Web3 Marketing'],
      link: '#'
    },
    {
      title: 'Senior Director, Community and Social Media',
      company: 'Yuga Labs',
      location: 'Remote',
      period: 'Feb 2024 - Jan 2025',
      description: 'Oversaw all external communications and social efforts; managed a 12-person team - the company\'s largest. Drove major, global brand activations across Bored Ape Yacht Club, Otherside, and CryptoPunks.',
      technologies: ['Team Leadership', 'Brand Activations', 'Crisis Communications', 'Community Management'],
      link: '#'
    },
    {
      title: 'Director, Community and Social Media',
      company: 'Yuga Labs',
      location: 'Remote',
      period: 'Nov 2022 - Feb 2024',
      description: 'Owned the social and community voice for all Yuga Labs brands (2.5M+ followers across platforms). Built and scaled an 8-person team, instilling a community-first, fandom-aware approach to brand storytelling.',
      technologies: ['Community Building', 'Brand Storytelling', 'Team Scaling', 'Cross-functional Collaboration'],
      link: '#'
    },
    {
      title: 'Senior Producer',
      company: 'COLOR Creative',
      location: 'Los Angeles, CA',
      period: 'Apr 2022 - Oct 2022',
      description: 'Produced high-profile branded podcasts and videos for Amazon, Microsoft, and Nike with budgets up to $250K. Managed all phases of production across multiple concurrent projects.',
      technologies: ['Content Production', 'Project Management', 'Client Relations', 'Creative Development'],
      link: '#'
    },
    {
      title: 'Social Media Creative Producer',
      company: 'IMDb (Amazon subsidiary)',
      location: 'Seattle, WA',
      period: 'Nov 2017 - Mar 2022',
      description: 'Developed and launched IMDb\'s first podcast network and Twitch.tv presence. Produced and hosted 150+ videos spotlighting iconic film/TV moments and interviewing top creators.',
      technologies: ['Content Creation', 'Podcast Development', 'Video Production', 'Entertainment Marketing'],
      link: '#'
    },
    {
      title: 'Social Media Marketing Manager',
      company: 'Amazon Prime Video',
      location: 'Seattle, WA',
      period: 'May 2016 - Nov 2017',
      description: 'Created and owned all paid (+$1MM budget) and organic social media for Amazon Channels. Owned social media marketing strategy for 20+ channel launches including HBO, Showtime, and STARZ.',
      technologies: ['Paid Social', 'Channel Marketing', 'Go-to-Market Strategy', 'Analytics'],
      link: '#'
    }
  ];

  return (
    <section id="experience" className="py-32 bg-white dark:bg-black">
      <div className="container-max px-4 sm:px-6 lg:px-8">
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
        >
          {/* Section Header */}
          <div className="mb-20">
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.8, delay: 0.1 }}
              className="text-sm font-medium tracking-widest uppercase text-neutral-500 dark:text-neutral-400 mb-4"
            >
              Experience
            </motion.div>
            <motion.h2
              initial={{ opacity: 0, y: 20 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.8, delay: 0.2 }}
              className="text-5xl sm:text-6xl lg:text-7xl font-bold text-neutral-900 dark:text-white leading-none"
            >
              Career
              <br />
              Journey
            </motion.h2>
          </div>

          {/* Timeline */}
          <div className="max-w-5xl mx-auto">
            {experiences.map((experience, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, x: index % 2 === 0 ? -50 : 50 }}
                animate={inView ? { opacity: 1, x: 0 } : {}}
                transition={{ duration: 0.8, delay: 0.1 + index * 0.1 }}
                className={`relative flex items-start space-x-8 mb-16 ${
                  index % 2 === 0 ? 'lg:flex-row' : 'lg:flex-row-reverse lg:space-x-reverse'
                }`}
              >
                {/* Timeline Line */}
                <div className="hidden lg:block absolute left-1/2 transform -translate-x-1/2 w-0.5 h-full bg-neutral-300 dark:bg-neutral-600"></div>

                {/* Timeline Dot */}
                <div className="hidden lg:block absolute left-1/2 transform -translate-x-1/2 w-4 h-4 bg-neutral-900 dark:bg-white rounded-full border-4 border-white dark:border-black mt-6"></div>

                {/* Content Card */}
                <div className={`flex-1 ${index % 2 === 0 ? 'lg:pr-8' : 'lg:pl-8'}`}>
                  <div className="bg-neutral-50 dark:bg-neutral-800 border border-neutral-200 dark:border-neutral-700 p-8 hover:border-neutral-300 dark:hover:border-neutral-600 transition-colors duration-300">
                    {/* Header */}
                    <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between mb-6">
                      <div>
                        <h3 className="text-2xl font-bold text-neutral-900 dark:text-white mb-2">
                          {experience.title}
                        </h3>
                        <div className="flex items-center space-x-6 text-sm text-neutral-600 dark:text-neutral-400">
                          <div className="flex items-center space-x-2">
                            <MapPin size={14} />
                            <span>{experience.company}</span>
                          </div>
                          <div className="flex items-center space-x-2">
                            <Calendar size={14} />
                            <span>{experience.period}</span>
                          </div>
                        </div>
                      </div>
                      <motion.a
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                        href={experience.link}
                        className="mt-4 sm:mt-0 p-2 bg-neutral-200 dark:bg-neutral-700 text-neutral-600 dark:text-neutral-400 rounded hover:bg-neutral-300 dark:hover:bg-neutral-600 transition-colors duration-200"
                      >
                        <ExternalLink size={16} />
                      </motion.a>
                    </div>

                    {/* Description */}
                    <p className="text-lg text-neutral-600 dark:text-neutral-300 mb-6 leading-relaxed">
                      {experience.description}
                    </p>

                    {/* Technologies */}
                    <div className="flex flex-wrap gap-2">
                      {experience.technologies.map((tech) => (
                        <span
                          key={tech}
                          className="px-3 py-1 bg-neutral-200 dark:bg-neutral-700 text-neutral-700 dark:text-neutral-300 text-sm font-medium"
                        >
                          {tech}
                        </span>
                      ))}
                    </div>
                  </div>
                </div>
              </motion.div>
            ))}
          </div>

          {/* Education Section */}
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={inView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8, delay: 0.8 }}
            className="mt-20 text-center"
          >
            <div className="bg-neutral-50 dark:bg-neutral-800 border border-neutral-200 dark:border-neutral-700 p-8 max-w-2xl mx-auto">
              <h3 className="text-2xl font-bold text-neutral-900 dark:text-white mb-4">
                Education
              </h3>
              <div className="text-lg text-neutral-600 dark:text-neutral-300">
                <p className="font-medium mb-2">Bachelor of Arts, American Studies</p>
                <p className="text-sm">University of California, Santa Cruz â€¢ 2007 - 2011</p>
              </div>
            </div>
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
};

export default Experience;
