import React, { useState } from 'react';

interface MediaItem {
  type: 'image' | 'video';
  src: string;
  alt?: string;
}

interface ProjectCarouselProps {
  media: MediaItem[];
}

const ProjectCarousel: React.FC<ProjectCarouselProps> = ({ media }) => {
  const [current, setCurrent] = useState(0);
  if (!media || media.length === 0) return null;

  const goToPrev = () => setCurrent((prev) => (prev === 0 ? media.length - 1 : prev - 1));
  const goToNext = () => setCurrent((prev) => (prev === media.length - 1 ? 0 : prev + 1));

  const item = media[current];

  return (
    <div className="relative w-full max-w-2xl mx-auto flex flex-col items-center">
      <div className="w-full aspect-[16/9] bg-neutral-100 rounded flex items-center justify-center overflow-hidden mb-2">
        {item.type === 'image' ? (
          <img src={item.src} alt={item.alt || ''} className="object-contain w-full h-full" />
        ) : (
          <video controls className="object-contain w-full h-full">
            <source src={item.src} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        )}
      </div>
      {media.length > 1 && (
        <div className="flex justify-center items-center gap-4 mt-2">
          <button onClick={goToPrev} className="px-3 py-1 bg-neutral-200 rounded hover:bg-neutral-300">Prev</button>
          <span className="text-sm text-neutral-500">{current + 1} / {media.length}</span>
          <button onClick={goToNext} className="px-3 py-1 bg-neutral-200 rounded hover:bg-neutral-300">Next</button>
        </div>
      )}
    </div>
  );
};

export default ProjectCarousel;
