import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { ExternalLink, Github, Eye } from 'lucide-react';

const Projects: React.FC = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  const projects = [
    {
      title: 'Bored Ape Yacht Club Global Activations',
      description: 'Led major, global brand activations and narrative rollouts across the Bored Ape Yacht Club ecosystem. Managed a 12-person team driving cross-functional campaign strategies.',
      media: [
        { type: 'image', src: '/api/placeholder/400/250', alt: 'BAYC Activation' }
      ],
      liveUrl: '#',
      githubUrl: '#',
    },
    {
      title: 'IMDb Podcast Network Launch',
      description: 'Developed and launched IMDb\'s first podcast network, including "Movies That Changed My Life" and "That Scene with Dan Patrick". Established new content verticals for the entertainment platform.',
      media: [
        { type: 'image', src: '/api/placeholder/400/250', alt: 'IMDb Podcast' }
      ],
      liveUrl: '#',
      githubUrl: '#',
    },
    {
      title: 'Amazon Channels Social Strategy',
      description: 'Created and owned all paid (+$1MM budget) and organic social media for Amazon Channels. Led go-to-market strategy for tentpole series like Game of Thrones and Ray Donovan.',
      media: [
        { type: 'image', src: '/api/placeholder/400/250', alt: 'Amazon Channels' }
      ],
      liveUrl: '#',
      githubUrl: '#',
    }
  ];

  return (
    <section id="projects" className="py-8 bg-white">
      <div className="container-max px-4 sm:px-6 lg:px-8">
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
        >
          {/* Section Header */}
          <div className="mb-20">
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.8, delay: 0.1 }}
              className="text-sm font-medium tracking-widest uppercase text-neutral-500 dark:text-neutral-400 mb-4"
            >
              Work
            </motion.div>
            <motion.h2
              initial={{ opacity: 0, y: 20 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ duration: 0.8, delay: 0.2 }}
              className="text-5xl sm:text-6xl lg:text-7xl font-bold text-neutral-900 dark:text-white leading-none"
            >
              Featured
              <br />
              Work
            </motion.h2>
          </div>

          {/* Projects List */}
          <div className="flex flex-col gap-16">
            {projects.map((project, index) => (
              <motion.div
                key={project.title}
                initial={{ opacity: 0, y: 30 }}
                animate={inView ? { opacity: 1, y: 0 } : {}}
                transition={{ duration: 0.8, delay: 0.1 + index * 0.1 }}
                className="bg-white border border-neutral-200 rounded-lg shadow-sm p-8"
              >
                <h3 className="text-3xl font-bold text-neutral-900 mb-4">{project.title}</h3>
                <p className="text-lg text-neutral-600 mb-6 leading-relaxed">{project.description}</p>
                {/* Carousel Placeholder */}
                <div className="mb-4">
                  <ProjectCarousel media={project.media} />
                </div>
              </motion.div>
            ))}
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Projects;
